import { StatusBar } from 'expo-status-bar';
import React, { Component }  from 'react';

import { View, Text } from 'react-native'
import * as firebase from 'firebase'
import {NavigationContainer } from '@react-navigation/native';
import {createStackNavigator } from '@react-navigation/stack';

import LandingScreen from './components/auth/Landing'
import RegisterScreen from './components/auth/Register'
import LoginScreen from './components/auth/Login'
import { gestureHandlerRootHOC } from 'react-native-gesture-handler';

const firebaseConfig = {
  apiKey: "AIzaSyC3Fwk4pBQ8r2GcLO7IcaoblfjAyEZZPSM",
  authDomain: "instagram-7808e.firebaseapp.com",
  databaseURL: "https://instagram-7808e-default-rtdb.firebaseio.com",
  projectId: "instagram-7808e",
  storageBucket: "instagram-7808e.appspot.com",
  messagingSenderId: "1014368355198",
  appId: "1:1014368355198:web:8f242ab9e2a4767d37292f",
  measurementId: "G-8X8N0K7MNY"
};
if(firebase.apps.length === 0){
   firebase.initializeApp(firebaseConfig)
}

const Stack = createStackNavigator();



export class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      loaded: false,
    }
  }

  componentDidMount(){
    firebase.auth().onAuthStateChanged((user) =>{
      if(!user){
        this.setState({
          loggedIn: false,
          loaded: true,
        })
      }else{
      this.setState({
        loggedIn: true,
        loaded: true,
      })
     }
    })
  }
  render() {
    const {loggedIn, loaded } =this.setState;
    if(!loaded){
      return(
        <View style={{ flex: 1, justifyContent: 'center' }}>
          <Text>Loading</Text>
        </View>
      )
    }

    if(!loggedIn){
    return (
      <NavigationContainer> 
      <Stack.Navigator initialRoutingName="Landing">
        <Stack.Screen name="Landing" component={LandingScreen} options={{ headerShown: false}} />
        <Stack.Screen name="Register" component={RegisterScreen}  />
        <Stack.Screen name="Login" component={LoginScreen} />
      </Stack.Navigator>

    </NavigationContainer>
    );
  }

  return (
    <View style={{ flex: 1, justifyContent: 'center' }}>
    <Text>User is Logged in</Text>
  </View>
  )
}
  }

export default App
